example.codeblock:
CREATE TABLE IMAGENS( NOME VARCHAR(32) NOT NULL PRIMARY KEY, IMAGEM BLOB(16M));
-- Encontrar todas as imagens de logotipos SELECT LENGTH(IMAGEM), NOME FROM IMAGENS WHERE NOME LIKE '%logo%';
-- Encontrar todas as imagens duplicadas (comparação de BLOBs) SELECT A.NOME AS DUPLICADA_1, B.NOME AS DUPLICADA_2 FROM IMAGENS AS A, IMAGENS AS B WHERE A.NOME < B.NOME AND A.IMAGEM = B.IMAGEM ORDER BY 1,2;
