-- restrição de chave primária no nível-de-coluna chamada PK_SAÍDA: CREATE TABLE SAMP.CAIXA_SAÍDA ( ENVIO TIMESTAMP, DESTINO CHAR(8), ASSUNTO CHAR(64) NOT NULL CONSTRAINT PK_CAIXA_SAÍDA PRIMARY KEY, TEXTO_NOTA VARCHAR(3000) );
-- a definição de chave primária no nível-de-tabela permite -- incluir duas colunas na definição da chave primária: CREATE TABLE SAMP.AGENDA ( COD_CLASSE CHAR(7) NOT NULL, DIA SMALLINT NOT NULL, INÍCIO TIME, FIM TIME, PRIMARY KEY (COD_CLASSE, DIA) );
-- Uso de uma restrição no nível-de-coluna para verificação aritmética. -- Uso de uma restrição no nível-de-tabela para ter certeza que os -- impostos do empregado não são maiores que os bônus. CREATE TABLE SAMP.EMP ( NUM_EMP CHAR(6) NOT NULL CONSTRAINT PK_EMP PRIMARY KEY, NOME CHAR(12) NOT NULL, INICIAL_MEIO VARCHAR(12) NOT NULL, SOBRENOME VARCHAR(15) NOT NULL, SALÁRIO DECIMAL(9,2) CONSTRAINT VERIF_SAL CHECK (SALÁRIO >= 10000), BÔNUS DECIMAL(9,2), IMPOSTOS DECIMAL(9,2), CONSTRAINT VERIF_BÔNUS CHECK (BÔNUS > IMPOSTOS) );
-- Uso de uma restrição de verificação para permitir -- apenas as abreviaturas apropriadas para as refeições CREATE TABLE VÔOS ( ID_VÔO CHAR(6) NOT NULL , NÚMERO_SEGMENTO INTEGER NOT NULL , AEROPORTO_ORIGEM CHAR(3), HORA_PARTIDA TIME, AEROPORTO_DESTINO CHAR(3), HORA_CHEGADA TIME, REFEIÇÃO CHAR(1) CONSTRAINT VERIF_REFEIÇÃO CHECK (REFEIÇÃO IN ('B', 'L', 'D', 'S')), PRIMARY KEY (ID_VÔO, NÚMERO_SEGMENTO) );
CREATE TABLE METROPOLITANO ( ID_HOTEL INT NOT NULL CONSTRAINT PK_HOTÉIS PRIMARY KEY, NOME_HOTEL VARCHAR(40) NOT NULL, ID_CIDADE INT CONSTRAINT FK_METRO REFERENCES CIDADES );
-- criação de uma tabela com uma restrição de chave primária -- e uma restrição de chave estrangeira no nível-de-tabela CREATE TABLE VÔOS_DISP ( ID_VÔO CHAR(6) NOT NULL, NÚMERO_SEGMENTO INT NOT NULL, DATA_VÔO DATE NOT NULL, ASSENTOS_ECONÔMICOS_OCUPADOS INT, ASSENTOS_EXECUTIVOS_OCUPADOS INT, ASSENTOS_PRIMEIRA_CLASSE_OCUPADOS INT, CONSTRAINT PK_VÔOS_DISP PRIMARY KEY (ID_VÔO, NÚMERO_SEGMENTO), CONSTRAINT FK_VÔOS FOREIGN KEY (ID_VÔO, NÚMERO_SEGMENTO) REFERENCES VÔOS (ID_VÔO, NÚMERO_SEGMENTO) );
-- adicionar uma restrição de unicidade a uma coluna ALTER TABLE SAMP.PROJETO ADD CONSTRAINT UNQ_PROJ UNIQUE (NOME_PROJ);
-- criar uma tabela cuja coluna ID_CIDADE faça referência -- à chave primária da tabela CIDADES utilizando uma -- restrição de chave estrangeira no nível-de-coluna CREATE TABLE CONDOMÍNIOS ( ID_COND INT NOT NULL CONSTRAINT PK_HOTÉIS PRIMARY KEY, NOME_COND VARCHAR(40) NOT NULL, ID_CIDADE INT CONSTRAINT FK_CIDADE REFERENCES CIDADES ON DELETE CASCADE ON UPDATE RESTRICT );
