SELECT CIDADES.ID_CIDADE FROM CIDADES WHERE ID_CIDADE < 5
-- outros tipos de ExpressãoTabela SELECT TABLENAME, ISINDEX FROM SYS.SYSTABLES T, SYS.SYSCONGLOMERATES C WHERE T.TABLEID = C.TABLEID ORDER BY TABLENAME, ISINDEX;
-- forçar a ordem de junção SELECT * FROM VÔOS, VÔOS_DISPONIBILIDADE WHERE VÔOS_DISPONIBILIDADE.ID_VÔO = VÔOS.ID_VÔO AND VÔOS_DISPONIBILIDADE.NÚMERO_SEGMENTO = VÔOS.NÚMERO_SEGMENTO AND VÔOS.ID_VÔO < 'AA1115'
-- a ExpressãoTabela pode ser uma OperaçãoJunção. Portanto, -- podem haver várias operações de junção na cláusula FROM SELECT PAÍSES.PAÍS, CIDADES.NOME_CIDADE, VÔOS.AEROPORTO_DESTINO FROM PAÍSES LEFT OUTER JOIN CIDADES ON PAÍSES.COD_ISO_PAÍS = CIDADES.COD_ISO_PAÍS LEFT OUTER JOIN VÔOS ON CIDADES.AEROPORTO = VÔOS.AEROPORTO_DESTINO;
