CREATE TABLE nome-da-tabela
    ( {definição-da-coluna | restrição no nível-de-tabela}
    [ , {definição-da-coluna | restrição no nível-de-tabela} ] * )

CREATE TABLE DISPONIBILIDADE_HOTEL ( ID_HOTEL INT NOT NULL, DATA_RESERVA DATE NOT NULL, QUARTOS_RESERVADOS INT DEFAULT 0, PRIMARY KEY (ID_HOTEL, DATA_RESERVA));
-- A definição de chave primária no nível-de-tabela permite -- incluir duas colunas na definição da chave primária
PRIMARY KEY (ID_HOTEL, DATA_RESERVA))
-- Atribuir um atributo de coluna de identidade a uma coluna INTEGER, -- e também definir uma restrição de chave primária na coluna
CREATE TABLE PESSOAS ( ID_PESSOA INT NOT NULL GENERATED ALWAYS AS IDENTITY CONSTRAINT PK_PESSOAS PRIMARY KEY, PESSOA VARCHAR(26));
-- Atribuir um atributo de coluna de identidade a uma coluna SMALLINT -- com valor inicial igual a 5 e valor do incremento igual a 5.
CREATE TABLE GRUPOS ( ID_GRUPO SMALLINT NOT NULL GENERATED ALWAYS AS IDENTITY (START WITH 5, INCREMENT BY 5), ENDEREÇO VARCHAR(100), TEL VARCHAR(15));
